select
id, management_name, management_type, identifier, createtime, updatetime
from management_account


        and id = #{managementAccount.id}


        and management_name = #{managementAccount.managementName}


        and management_type = #{managementAccount.managementType}


        and identifier = #{managementAccount.identifier}


        and createtime = #{managementAccount.createtime}


        and updatetime = #{managementAccount.updatetime}


limit #{offset}, #{limit}

