mapper.select:
select
id, operater_type, operater_id, operater_name, operate_type, operate_detail, createtime
from acountManagement.operation_log


        and id = #{id}


        and operater_type = #{operaterType}


        and operater_id = #{operaterId}


        and operater_name = #{operaterName}


        and operate_type = #{operateType}


        and operate_detail = #{operateDetail}


        and createtime = #{createtime}
